    public Boolean validateFollowedSectionsCarrousel()throws InterruptedException{
       Boolean sectionLblDisplayed = false;
        sectionLblDisplayed = isEnterteinmentSectionLblDisplayed();
    //   scroll(1,1);
       clickOnFollowedEntertainmentCell();
       androidBACK();
       Thread.sleep(3000);
       //scrollUntilElement(seeMoreEntertainmentBtn);
     // scroll(1,1);
        lookForEntertainmentBtn();
        Thread.sleep(2000);
        androidBACK();
        Thread.sleep(3000);
       swipeRight();
       Thread.sleep(2000);
     //  WaitHelper.waitForElementDisplayed(driver,followedPoliticsLbl,30);
        sectionLblDisplayed = isFollowedPoliticsLblIsDisplayed();
        clickOnFollowedPoliticsCell();
        Thread.sleep(3000);
        androidBACK();
        Thread.sleep(3000);
        clickOnSeeMorePoliticsBtn();
        Thread.sleep(2000);
        androidBACK();
        Thread.sleep(2000);
        swipeRight();
        sectionLblDisplayed = isFollowedHFPersonalLblIsDisplayed();
        Thread.sleep(3000);
        clickOnFollowedHuffpostPersonalCell();
        Thread.sleep(5000);
        androidBACK();
      //  Thread.sleep(3000);
     //   clickOnSeeMoreHPPersonalBtn();
       // androidBACK();
        Thread.sleep(4000);
        swipeRight();
        sectionLblDisplayed = isFollowedCommunitiesLblIsDisplayed();
        clickOnFollowedCommunityCell();
        Thread.sleep(2000);
        androidBACK();
        Thread.sleep(3000);
        clickOnSeeMoreCommunitiesBtn();
        Thread.sleep(2000);
        androidBACK();
        return sectionLblDisplayed;
    }


    public void lookForEntertainmentBtn(){
       littleScroll(1,1);
       try {
           if (driver.findElement(seeMoreEntertainmentBtn).isDisplayed())
               clickOnSeeMoreEntertaimentBtn();
       } catch (Exception e){
           littleScroll(1,1);
           clickOnSeeMoreEntertaimentBtn();
       }
    }

